        Update config files and directories
        Update files and directories required by the new UKO server


            Backup server directory
            Submit script to backup the UKO server directory
            $!{instance-UKO_ADMIN_SERVER}
            $!{instance-UKO_APPROVER_SERVER}

            Submit shell script to execute step
            1
            true


                    # Get the current version
                    currentversion=$(iconv -t IBM037 -f utf-8 ${instance-UKO_SERVER_DIRECTORY}/EkmfWeb.properties | grep com.ibm.websphere.productVersion | sed -E 's/.*=([^\$])/\1/')

                    # create backup directory
                    mkdir ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey};

                    # move everything into backup directory
                    mv ${instance-UKO_SERVER_DIRECTORY}/EkmfWeb.properties ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/EkmfWeb.properties;
                    mv ${instance-UKO_SERVER_DIRECTORY}/apps ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/apps;

                    if [ -d "${instance-UKO_SERVER_DIRECTORY}/includes" ] 
                    then
                        mv ${instance-UKO_SERVER_DIRECTORY}/includes ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/includes; 
                    fi

                    if [ -d "${instance-UKO_SERVER_DIRECTORY}/configDropins" ] 
                    then
                        mv ${instance-UKO_SERVER_DIRECTORY}/configDropins ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/configDropins; 
                    fi


                    mv ${instance-UKO_SERVER_DIRECTORY}/resources ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/resources;
                    mv ${instance-UKO_SERVER_DIRECTORY}/server.xml ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/server.xml;		    
                    mv ${instance-UKO_SERVER_DIRECTORY}/server.env ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/server.env;			    
                    mv ${instance-UKO_SERVER_DIRECTORY}/jvm.options ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/jvm.options;			    

                    # Create a migration log
                    echo "Current UKO Version is: $currentversion" > ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/migration.log;

                shell-JCL




		    Substitute jvm.options
	        replace values in jvm.options with template variable values
	        $!{instance-UKO_ADMIN_SERVER}
	        $!{instance-UKO_APPROVER_SERVER}


            Submit shell script to execute step
            1
            true



                    #extract all name=value pairs that are not commented out from old file
                    sed '/^[[:blank:]]*#/d;/^$/d' ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/jvm.options > ${instance-TEMP_DIR}/zosmf-${_workflow-workflowKey}_jvm.options; 

                    echo "********************************************" >> ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/migration.log;
                    echo "Parameters from jvm.options to be migrated: " >> ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/migration.log;
                    cat ${instance-TEMP_DIR}/zosmf-${_workflow-workflowKey}_jvm.options >> ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/migration.log;
                    # set the number of lines
                    lines=$(sed -n '$='  ${instance-TEMP_DIR}/zosmf-${_workflow-workflowKey}_jvm.options; ); 
                    echo "********************************************" >> ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/migration.log;
                    echo "Number of parameters to be transferred:" ${lines} >> ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/migration.log;
                    sedstring="";
                    nottransfered="";
                    transferred="";

                    i=0;
                    while  [ i -lt ${lines} ] 
                    do
                        let i=i+1;
                        # echo "i:$i";
                        ukovariable=$(sed "${i}q;d"  ${instance-TEMP_DIR}/zosmf-${_workflow-workflowKey}_jvm.options   | sed -E 's/([^=]*)=(.*)/\1/');
                        ukovalue=$(sed "${i}q;d"  ${instance-TEMP_DIR}/zosmf-${_workflow-workflowKey}_jvm.options   | sed -E 's/([^=]*)=(.*)/\2/');
                        echo  "$ukovariable is $ukovalue";
                        sedstring="${sedstring} s#^[ \#]*${ukovariable}=.*#${ukovariable}=${ukovalue}#g;";

                        # remove leading -D so grep command does not break
                        grepstring=$(echo $ukovariable | cut -c3-);

                        #check whether the extracted variable is contained in the latest jvm.options
                        if !(grep -Fq "${grepstring}=" ${instance-UKO_INSTALL_DIR}/jvm.options)
                        then
                            nottransfered="${nottransfered}\n${ukovariable}=${ukovalue}";
                        else
                            transfered="${transfered}\n$ukovariable";
                            # create rest string to update variables
                            # echo "{\"name\":\"$ukovariable\",\"value\":\"$ukovalue\",\"visibility\":\"public\"}," >> ${instance-TEMP_DIR}/zosmf-${_workflow-workflowKey}.properties;
                        fi

                    done

                    # do the actual replacement
                    # echo "sedstring: $sedstring";
                    sed -e "$sedstring" ${instance-UKO_INSTALL_DIR}/jvm.options > ${instance-UKO_SERVER_DIRECTORY}/jvm.options;
                    echo "The following variable(s) have been transferred: $transfered\n" >> ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/migration.log;

                    if ["$nottransfered" = ""]
                    then
                        echo "All parameters were transferred" >> ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/migration.log;
                    else
                        echo "The following variable(s) can NOT be transferred automatically: ${nottransfered} \nVerify whether they still exist in the new version." >> ${instance-UKO_SERVER_DIRECTORY}/backup_${_workflow-workflowKey}/migration.log;
                        echo "# Verify the following parameters:\n${nottransfered}" >> ${instance-UKO_SERVER_DIRECTORY}/jvm.options;
                    fi     


            	shell-JCL





