                        ukovalue=$(sed "${i}q;d"  ${instance-TEMP_DIR}/zosmf-${_workflow-workflowKey}_server.env  | sed -E 's/([^=]*)=(.*)/\2/');
                        sedstring="${sedstring} s#^[ \#]*${ukovariable}=.*#${ukovariable}=${ukovalue}#g;";

