                        ln -s ${instance-UKO_INSTALL_DIR}/configDropins configDropins; 
                    fi

                    ln -s ${instance-UKO_INSTALL_DIR}/resources resources
                    ln -s ${instance-UKO_INSTALL_DIR}/server.xml server.xml			    


